(1, 1, '2015-11-03 04:01:17', '2015-11-03 04:01:17', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2015-11-03 04:01:17', '2015-11-03 04:01:17', '', 0, 'http://127.0.0.1/boyer/?p=1', 0, 'post', '', 1),
(2, 1, '2015-11-03 04:01:17', '2015-11-03 04:01:17', 'Welcome to our lodge - we invite you to our stated meetings on the Second Monday each month, except July and August at 7 P.M. Dinner is served before the meetings. If you''re not currently a member of our lodge - we''d like to invite you to contact us and ask who the Masons are.\r\n\r\nWe hope you and your family will have a warm and fun-filled experience with us together at our community events.\r\n<p style="text-align: center;">Feel free to visit our headquarters website:</p>\r\n<p style="text-align: center;"><a href="http://www.freemason-wa.org/" target="_blank">The Most Worshipful Grand Lodge of Washington </a></p>\r\n<p style="text-align: center;">dedicated</p>\r\n<p style="text-align: center;">Boyer Mountain Lodge No. 134</p>\r\n<p style="text-align: center;">In the new lodge hall located at</p>\r\n<p style="text-align: center;">310 North Main Avenue</p>\r\n<p style="text-align: center;">Deer Park, Washington</p>\r\n<p style="text-align: left;"></p>', 'Home Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2015-11-05 21:27:13', '2015-11-05 21:27:13', '', 0, 'http://127.0.0.1/boyer/?page_id=2', 0, 'page', '', 0),
(3, 1, '2015-11-03 04:01:31', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2015-11-03 04:01:31', '0000-00-00 00:00:00', '', 0, 'http://127.0.0.1/boyer/?p=3', 0, 'post', '', 0),
